import React, { useState, useEffect } from 'react';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from './ui/card';
import { Button } from './ui/button';
import { useAuth } from '../store/auth';
import { 
  Users, 
  DollarSign, 
  TrendingUp, 
  Calendar,
  Clock,
  CheckCircle,
  AlertCircle,
  Plus
} from 'lucide-react';

interface KPI {
  title: string;
  value: string;
  change: string;
  icon: React.ReactNode;
  color: string;
}

interface ChartData {
  labels: string[];
  datasets: {
    label: string;
    data: number[];
    backgroundColor?: string;
    borderColor?: string;
    fill?: boolean;
  }[];
}

const Dashboard: React.FC = () => {
  const { user } = useAuth();
  const [kpis, setKpis] = useState<KPI[]>([
    {
      title: 'Total Employees',
      value: '24',
      change: '+2 this month',
      icon: <Users className="h-4 w-4" />,
      color: 'text-blue-600'
    },
    {
      title: 'Monthly Revenue',
      value: '$125,000',
      change: '+12% vs last month',
      icon: <DollarSign className="h-4 w-4" />,
      color: 'text-green-600'
    },
    {
      title: 'Active Projects',
      value: '8',
      change: '3 due this week',
      icon: <TrendingUp className="h-4 w-4" />,
      color: 'text-purple-600'
    },
    {
      title: 'Attendance Rate',
      value: '94%',
      change: '+2% vs last week',
      icon: <Clock className="h-4 w-4" />,
      color: 'text-orange-600'
    }
  ]);

  const [recentActivity] = useState([
    { id: 1, action: 'New employee added', time: '2 hours ago', type: 'hr' },
    { id: 2, action: 'Project milestone reached', time: '1 day ago', type: 'project' },
    { id: 3, action: 'Invoice #1234 paid', time: '3 days ago', type: 'accounting' },
    { id: 4, action: 'Low stock alert', time: '1 week ago', type: 'inventory' }
  ]);

  const [upcomingEvents] = useState([
    { id: 1, title: 'Team Meeting', date: 'Today', time: '10:00 AM', type: 'meeting' },
    { id: 2, title: 'Invoice Due', date: 'Tomorrow', time: '9:00 AM', type: 'invoice' },
    { id: 3, title: 'Project Deadline', date: 'Friday', time: '5:00 PM', type: 'deadline' },
    { id: 4, title: 'Employee Review', date: 'Next Week', time: '2:00 PM', type: 'review' }
  ]);

  const [quickActions] = useState([
    { title: 'Add Employee', icon: <Users className="h-4 w-4" />, href: '/hr/employees/new' },
    { title: 'Create Invoice', icon: <DollarSign className="h-4 w-4" />, href: '/accounting/invoices/new' },
    { title: 'New Project', icon: <TrendingUp className="h-4 w-4" />, href: '/projects/new' },
    { title: 'Add Product', icon: <Plus className="h-4 w-4" />, href: '/inventory/products/new' }
  ]);

  return (
    <div className="space-y-6">
      {/* Welcome Section */}
      <div className="flex justify-between items-center">
        <div>
          <h1 className="text-3xl font-bold text-gray-900">Welcome back, {user?.email?.split('@')[0]}!</h1>
          <p className="text-gray-600">Here's what's happening with your business today.</p>
        </div>
        <div className="flex gap-2">
          <Button variant="outline">
            <Calendar className="h-4 w-4 mr-2" />
            Today: {new Date().toLocaleDateString()}
          </Button>
        </div>
      </div>

      {/* KPI Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        {kpis.map((kpi, index) => (
          <Card key={index} className="hover:shadow-lg transition-shadow">
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium text-gray-600">
                {kpi.title}
              </CardTitle>
              <div className={kpi.color}>
                {kpi.icon}
              </div>
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold">{kpi.value}</div>
              <p className="text-xs text-gray-500 mt-1">{kpi.change}</p>
            </CardContent>
          </Card>
        ))}
      </div>

      {/* Main Content Grid */}
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        {/* Quick Actions */}
        <Card>
          <CardHeader>
            <CardTitle>Quick Actions</CardTitle>
            <CardDescription>Common tasks and shortcuts</CardDescription>
          </CardHeader>
          <CardContent>
            <div className="space-y-3">
              {quickActions.map((action, index) => (
                <Button key={index} variant="outline" className="w-full justify-start">
                  {action.icon}
                  <span className="ml-2">{action.title}</span>
                </Button>
              ))}
            </div>
          </CardContent>
        </Card>

        {/* Recent Activity */}
        <Card>
          <CardHeader>
            <CardTitle>Recent Activity</CardTitle>
            <CardDescription>Latest updates and notifications</CardDescription>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              {recentActivity.map((activity) => (
                <div key={activity.id} className="flex items-start space-x-3">
                  <div className="flex-shrink-0">
                    <div className="w-2 h-2 bg-blue-500 rounded-full mt-2"></div>
                  </div>
                  <div className="flex-1 min-w-0">
                    <p className="text-sm font-medium text-gray-900">{activity.action}</p>
                    <p className="text-xs text-gray-500">{activity.time}</p>
                  </div>
                </div>
              ))}
            </div>
          </CardContent>
        </Card>

        {/* Upcoming Events */}
        <Card>
          <CardHeader>
            <CardTitle>Upcoming Events</CardTitle>
            <CardDescription>Important dates and deadlines</CardDescription>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              {upcomingEvents.map((event) => (
                <div key={event.id} className="flex items-start space-x-3">
                  <div className="flex-shrink-0">
                    <div className={`w-2 h-2 rounded-full mt-2 ${
                      event.type === 'meeting' ? 'bg-blue-500' :
                      event.type === 'invoice' ? 'bg-red-500' :
                      event.type === 'deadline' ? 'bg-orange-500' : 'bg-green-500'
                    }`}></div>
                  </div>
                  <div className="flex-1 min-w-0">
                    <p className="text-sm font-medium text-gray-900">{event.title}</p>
                    <p className="text-xs text-gray-500">{event.date} at {event.time}</p>
                  </div>
                </div>
              ))}
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Charts Section */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Revenue Chart Placeholder */}
        <Card>
          <CardHeader>
            <CardTitle>Monthly Revenue</CardTitle>
            <CardDescription>Revenue trends over the last 6 months</CardDescription>
          </CardHeader>
          <CardContent>
            <div className="h-64 flex items-center justify-center bg-gray-50 rounded-lg">
              <div className="text-center">
                <TrendingUp className="h-12 w-12 text-gray-400 mx-auto mb-2" />
                <p className="text-gray-500">Chart will be implemented here</p>
                <p className="text-sm text-gray-400">Using react-chartjs-2</p>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Attendance Chart Placeholder */}
        <Card>
          <CardHeader>
            <CardTitle>Attendance Trends</CardTitle>
            <CardDescription>Employee attendance over the last 30 days</CardDescription>
          </CardHeader>
          <CardContent>
            <div className="h-64 flex items-center justify-center bg-gray-50 rounded-lg">
              <div className="text-center">
                <Clock className="h-12 w-12 text-gray-400 mx-auto mb-2" />
                <p className="text-gray-500">Chart will be implemented here</p>
                <p className="text-sm text-gray-400">Using react-chartjs-2</p>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
};

export default Dashboard;
