import React, { useState, useEffect } from 'react';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from './ui/card';
import { Button } from './ui/button';
import { useAuth } from '../store/auth';
import { useNavigate } from 'react-router-dom';
import { 
  Users, 
  DollarSign, 
  TrendingUp, 
  Calendar,
  Clock,
  CheckCircle,
  AlertCircle,
  Plus,
  Loader2
} from 'lucide-react';
import { LineChart, DoughnutChart, generateRevenueData, generateDepartmentData } from './ui/charts';

interface KPI {
  title: string;
  value: string;
  change: string;
  icon: React.ReactNode;
  color: string;
}

interface DashboardData {
  summary: {
    employees: number;
    customers: number;
    projects: number;
    products: number;
    revenue: number;
    expenses: number;
    profit: number;
  };
  recentActivities: {
    employees: Array<{
      id: number;
      name: string;
      department: string;
      date: string;
      type: string;
    }>;
    projects: Array<{
      id: number;
      name: string;
      customer: string;
      date: string;
      type: string;
    }>;
    transactions: Array<{
      id: number;
      amount: number;
      type: string;
      description: string;
      date: string;
      customer: string;
    }>;
  };
  monthlyRevenue: Array<{
    month: string;
    revenue: number;
  }>;
  stockAlerts: Array<{
    id: number;
    name: string;
    currentStock: number;
    threshold: number;
  }>;
  upcomingTasks: Array<{
    id: number;
    name: string;
    project: string;
    assignedTo: string;
    status: string;
    dueDate: string;
  }>;
}

const Dashboard: React.FC = () => {
  const { user } = useAuth();
  const navigate = useNavigate();
  const [dashboardData, setDashboardData] = useState<DashboardData | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  // Fetch dashboard data
  useEffect(() => {
    const fetchDashboardData = async () => {
      try {
        setLoading(true);
        const response = await fetch('/api/reports/dashboard', {
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('token')}`
          }
        });
        
        if (!response.ok) {
          throw new Error('Failed to fetch dashboard data');
        }
        
        const data = await response.json();
        setDashboardData(data);
      } catch (err) {
        setError('Failed to load dashboard data');
        console.error('Dashboard fetch error:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchDashboardData();
  }, []);

  // Generate KPIs from real data
  const generateKPIs = (data: DashboardData): KPI[] => [
    {
      title: 'Total Employees',
      value: data.summary.employees.toString(),
      change: 'Active team members',
      icon: <Users className="h-4 w-4" />,
      color: 'text-blue-600'
    },
    {
      title: 'Monthly Revenue',
      value: `$${data.summary.revenue.toLocaleString()}`,
      change: `Net profit: $${data.summary.profit.toLocaleString()}`,
      icon: <DollarSign className="h-4 w-4" />,
      color: 'text-green-600'
    },
    {
      title: 'Active Projects',
      value: data.summary.projects.toString(),
      change: `${data.summary.customers} customers`,
      icon: <TrendingUp className="h-4 w-4" />,
      color: 'text-purple-600'
    },
    {
      title: 'Products in Stock',
      value: data.summary.products.toString(),
      change: `${data.stockAlerts.length} low stock alerts`,
      icon: <Clock className="h-4 w-4" />,
      color: 'text-orange-600'
    }
  ];

  // Quick actions with navigation
  const quickActions = [
    { title: 'Add Employee', icon: <Users className="h-4 w-4" />, action: () => navigate('/hr') },
    { title: 'Create Transaction', icon: <DollarSign className="h-4 w-4" />, action: () => navigate('/accounting') },
    { title: 'New Project', icon: <TrendingUp className="h-4 w-4" />, action: () => navigate('/projects') },
    { title: 'Add Product', icon: <Plus className="h-4 w-4" />, action: () => navigate('/inventory') }
  ];

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="text-center">
          <Loader2 className="h-8 w-8 animate-spin mx-auto mb-4 text-blue-600" />
          <p className="text-gray-600">Loading dashboard...</p>
        </div>
      </div>
    );
  }

  if (error || !dashboardData) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="text-center">
          <AlertCircle className="h-8 w-8 mx-auto mb-4 text-red-600" />
          <p className="text-gray-600">{error || 'Failed to load dashboard'}</p>
        </div>
      </div>
    );
  }

  const kpis = generateKPIs(dashboardData);

  // Combine recent activities
  const allRecentActivities = [
    ...dashboardData.recentActivities.employees.map(emp => ({
      id: emp.id,
      action: `New employee: ${emp.name}`,
      time: emp.date,
      type: 'hr' as const
    })),
    ...dashboardData.recentActivities.projects.map(proj => ({
      id: proj.id,
      action: `Project: ${proj.name}`,
      time: proj.date,
      type: 'project' as const
    })),
    ...dashboardData.recentActivities.transactions.map(txn => ({
      id: txn.id,
      action: `${txn.type === 'income' ? 'Income' : 'Expense'}: $${txn.amount}`,
      time: txn.date,
      type: 'accounting' as const
    }))
  ].sort((a, b) => new Date(b.time).getTime() - new Date(a.time).getTime()).slice(0, 5);

  return (
    <div className="space-y-6">
      {/* Welcome Section */}
      <div className="flex justify-between items-center">
        <div>
          <h1 className="text-3xl font-bold text-gray-900">Welcome back, {user?.email?.split('@')[0]}!</h1>
          <p className="text-gray-600">Here's what's happening with your business today.</p>
        </div>
        <div className="flex gap-2">
          <Button variant="outline">
            <Calendar className="h-4 w-4 mr-2" />
            Today: {new Date().toLocaleDateString()}
          </Button>
        </div>
      </div>

      {/* KPI Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        {kpis.map((kpi, index) => (
          <Card key={index} className="hover:shadow-lg transition-shadow">
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium text-gray-600">
                {kpi.title}
              </CardTitle>
              <div className={kpi.color}>
                {kpi.icon}
              </div>
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold">{kpi.value}</div>
              <p className="text-xs text-gray-500 mt-1">{kpi.change}</p>
            </CardContent>
          </Card>
        ))}
      </div>

      {/* Main Content Grid */}
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        {/* Quick Actions */}
        <Card>
          <CardHeader>
            <CardTitle>Quick Actions</CardTitle>
            <CardDescription>Common tasks and shortcuts</CardDescription>
          </CardHeader>
          <CardContent>
            <div className="space-y-3">
              {quickActions.map((action, index) => (
                <Button 
                  key={index} 
                  variant="outline" 
                  className="w-full justify-start"
                  onClick={action.action}
                >
                  {action.icon}
                  <span className="ml-2">{action.title}</span>
                </Button>
              ))}
            </div>
          </CardContent>
        </Card>

        {/* Recent Activity */}
        <Card>
          <CardHeader>
            <CardTitle>Recent Activity</CardTitle>
            <CardDescription>Latest updates and notifications</CardDescription>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              {allRecentActivities.length > 0 ? (
                allRecentActivities.map((activity) => (
                  <div key={activity.id} className="flex items-start space-x-3">
                    <div className="flex-shrink-0">
                      <div className={`w-2 h-2 rounded-full mt-2 ${
                        activity.type === 'hr' ? 'bg-blue-500' :
                        activity.type === 'project' ? 'bg-purple-500' :
                        activity.type === 'accounting' ? 'bg-green-500' : 'bg-orange-500'
                      }`}></div>
                    </div>
                    <div className="flex-1 min-w-0">
                      <p className="text-sm font-medium text-gray-900">{activity.action}</p>
                      <p className="text-xs text-gray-500">{activity.time}</p>
                    </div>
                  </div>
                ))
              ) : (
                <p className="text-sm text-gray-500">No recent activity</p>
              )}
            </div>
          </CardContent>
        </Card>

        {/* Upcoming Tasks */}
        <Card>
          <CardHeader>
            <CardTitle>Upcoming Tasks</CardTitle>
            <CardDescription>Recent project tasks and assignments</CardDescription>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              {dashboardData.upcomingTasks.length > 0 ? (
                dashboardData.upcomingTasks.map((task) => (
                  <div key={task.id} className="flex items-start space-x-3">
                    <div className="flex-shrink-0">
                      <div className={`w-2 h-2 rounded-full mt-2 ${
                        task.status === 'completed' ? 'bg-green-500' :
                        task.status === 'in-progress' ? 'bg-blue-500' : 'bg-orange-500'
                      }`}></div>
                    </div>
                    <div className="flex-1 min-w-0">
                      <p className="text-sm font-medium text-gray-900">{task.name}</p>
                      <p className="text-xs text-gray-500">{task.project} â€¢ {task.assignedTo}</p>
                    </div>
                  </div>
                ))
              ) : (
                <p className="text-sm text-gray-500">No upcoming tasks</p>
              )}
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Charts Section */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        <Card>
          <CardHeader>
            <CardTitle>Revenue Trends</CardTitle>
            <CardDescription>Monthly revenue and profit trends</CardDescription>
          </CardHeader>
          <CardContent>
            <LineChart 
              data={generateRevenueData()} 
              title="Revenue vs Expenses"
              height={250}
            />
          </CardContent>
        </Card>

        <Card>
          <CardHeader>
            <CardTitle>Department Distribution</CardTitle>
            <CardDescription>Employee count by department</CardDescription>
          </CardHeader>
          <CardContent>
            <DoughnutChart 
              data={generateDepartmentData()} 
              title="Department Breakdown"
              height={250}
            />
          </CardContent>
        </Card>
      </div>
    </div>
  );
};

export default Dashboard;
